#!/bin/bash set -e echo Triton revision: %system.build.vcs.number.QtAutomotive_Components_HttpsGitLfsQtIoGerritTritonUiGit% git clone git@git.pelagicore.net:uxteam/meta-qtas-demo.git git clone git@git.pelagicore.net:uxteam/qtas-demo-manifest.git git clone https://github.com/nzamotaev/TeamCityDockerScripts
cd meta-qtas-demo git config user.email "nzamotaev@luxoft.com" git config user.name "TeamCity server" FILENAME="`find ./ -name triton-ui_git.bb`" test -f "$FILENAME" sed 's/SRCREV *= *".*"/SRCREV = "%system.build.vcs.number.QtAutomotive_Components_HttpsGitLfsQtIoGerritTritonUiGit%"/' -i "$FILENAME" git commit -m "Triton: Automatic update to the latest version of Triton" "$FILENAME" git log |head -n 15 REVISION="`git rev-parse HEAD`" cd ..
cd qtas-demo-manifest ../TeamCityDockerScripts/DocumentationSDK/UpdateTritonVersion/pelux_parse.py pelux.xml "$REVISION" git config user.email "nzamotaev@luxoft.com" git config user.name "TeamCity server" git commit -m "Automatic update to the latest version of meta-qtas-demo" pelux.xml git log |head -n 15
